function normalizeBrazilianPhoneNumber(phoneNumber) {
  // Remove todos os caracteres n√£o num√©ricos
  let cleanedNumber = phoneNumber.replace(/\D/g, "");

  // Verifica se √© um n√∫mero brasileiro (come√ßa com 55)
  if (cleanedNumber.startsWith("55")) {
    // Remove o prefixo 55 para facilitar a valida√ß√£o do DDD e do n√∫mero
    let numberWithoutCountryCode = cleanedNumber.substring(2);

    // Verifica se o n√∫mero (sem o 55) tem 10 d√≠gitos (DDD + 8 d√≠gitos do n√∫mero antigo)
    // e se o primeiro d√≠gito do n√∫mero √© um dos que indicam celular (6, 7, 8, 9)
    if (numberWithoutCountryCode.length === 10 && ["6", "7", "8", "9"].includes(numberWithoutCountryCode.substring(2, 3))) {
      // Insere o '9' ap√≥s o DDD (no √≠ndice 2 de numberWithoutCountryCode)
      cleanedNumber = "55" + numberWithoutCountryCode.substring(0, 2) + "9" + numberWithoutCountryCode.substring(2);
    }
  }

  return cleanedNumber;
}

