let sourceData = $input.all()[0].json;
let platform = 'unknown';
let standardData = {
  platform: '',
  userId: '',
  userName: '',
  userPhone: '',
  userEmail: '',
  messageType: '',
  messageContent: '',
  mediaId: '',
  mediaCaption: '',
  hasBinaryData: false,
  originalData: sourceData
};

// Verificar se tem dados bin√°rios
let hasBinary = false;
try {
  hasBinary = Object.keys($binary).length > 0;
} catch (error) {
  hasBinary = false;
}

// Detectar plataforma
if (sourceData.messaging_product === 'whatsapp') {
  platform = 'whatsapp';
} else if (sourceData.body && sourceData.body.platform === 'web') {
  platform = 'web';
} else if (sourceData.body && sourceData.body.platform) {
  platform = sourceData.body.platform;
}

standardData.platform = platform;
standardData.hasBinaryData = hasBinary;

// PADRONIZAR DADOS DO WHATSAPP
if (platform === 'whatsapp') {
  if (sourceData.contacts && sourceData.contacts[0]) {
    standardData.userId = sourceData.contacts[0].wa_id;
    standardData.userName = sourceData.contacts[0].profile.name;
    standardData.userPhone = normalizeBrazilianPhoneNumber(sourceData.contacts[0].wa_id);
  }

  if (sourceData.messages && sourceData.messages[0]) {
    const message = sourceData.messages[0];
    standardData.messageType = message.type;

    switch (message.type) {
      case 'text':
        standardData.messageContent = message.text.body;
        break;
      case 'image':
        standardData.mediaId = message.image.id;
        standardData.mediaCaption = message.image.caption || '';
        standardData.messageContent = message.image.caption || '[Imagem enviada]';
        break;
      case 'audio':
        standardData.mediaId = message.audio.id;
        standardData.messageContent = '[√Åudio enviado]';
        break;
      case 'document':
        standardData.mediaId = message.document.id;
        standardData.mediaCaption = message.document.caption || '';
        standardData.messageContent = message.document.caption || '[Documento enviado]';
        break;
    }
  }
}

// PADRONIZAR DADOS DA WEB
if (platform === 'web') {
  standardData.userId = sourceData.body.user_id;
  standardData.userName = sourceData.body.user_name;
  standardData.userPhone = normalizeBrazilianPhoneNumber(sourceData.body.user_phone);
  standardData.userEmail = sourceData.body.user_email;
  standardData.messageType = sourceData.body.message_type;

  // Web com arquivo bin√°rio (√°udio/imagem/documento)
  if (hasBinary && ['audio', 'image', 'document'].includes(sourceData.body.message_type)) {
    standardData.mediaId = 'binary_data';
    standardData.mediaCaption = sourceData.body.message_content || '';
    standardData.messageContent = sourceData.body.message_content || `[${sourceData.body.message_type} enviado]`;
  } else {
    standardData.messageContent = sourceData.body.message_content;
  }
}

return [standardData];

