const currentIndex = $runIndex + 1;

console.log(`‚úÖ Processando mensagem ${currentIndex}:`, currentMessage);

// 2. Usar sessionStorage simples baseado no workflow
const sessionKey = `web_messages_${$workflow.id}_${new Date().toDateString()}`;

// 3. Inicializar storage global
if (!global.webMessageCollector) {
  global.webMessageCollector = {};
}

if (!global.webMessageCollector[sessionKey]) {
  global.webMessageCollector[sessionKey] = {
    messages: [],
    startTime: new Date().toISOString()
  };
}

// 4. Adicionar mensagem atual
global.webMessageCollector[sessionKey].messages.push(currentMessage);
const collectedMessages = global.webMessageCollector[sessionKey].messages;

console.log(`üìä Total coletado: ${collectedMessages.length} mensagens`);

// 5. Verificar se deve finalizar (baseado no Loop Over Items)
const isLastInBatch = $('Loop Over Items').item.json.batchComplete || false;

if (isLastInBatch || collectedMessages.length >= 10) { // Finalizar se muitas mensagens
  console.log(`üéØ Finalizando com ${collectedMessages.length} mensagens`);

  // 6. Criar mensagem final
  let finalMessage = "";

  if (collectedMessages.length === 1) {
    finalMessage = collectedMessages[0];
  } else {
    collectedMessages.forEach((msg, index) => {
      const partNumber = index + 1;
      finalMessage += `üìù *Parte ${partNumber}:*\n\n${msg}`;

      if (index < collectedMessages.length - 1) {
        finalMessage += "\n\n" + "‚îÄ".repeat(30) + "\n\n";
      }
    });
  }

  // 7. Limpar cache
  delete global.webMessageCollector[sessionKey];

  return [{
    json: {
      success: true,
      message: finalMessage,
      platform: 'web',
      total_parts: collectedMessages.length,
      is_final: true,
      timestamp: new Date().toISOString()
    }
  }];

} else {
  // 8. Ainda coletando
  return [{
    json: {
      collecting: true,
      message_index: currentIndex,
      current_message: currentMessage,
      total_collected: collectedMessages.length,
      is_final: false
    }
  }];
}

